package PROGRAM;

public class SWITCHCASE2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int sub=3;
		switch(sub) {
		case 1:
			int a=1;
			int b=2;
			int sum=a+b;
			System.out.println(sum);
			break;
		case 2:
			int c=1;
			int d=2;
			int sum1=c-d;
			System.out.println(sum1);
			break;
		case 3:
			int e=3;
			int f=5;
			sum=e*f;
			System.out.println(sum);
			break;
		case 4:
			int g=5;
			int h=4;
			sum=g/h;
			System.out.println(sum);
			break;
			default:
				System.out.println("invalid");
			}
	}

}
